import React, {useState} from 'react'
import logo from './logo.svg';
import { Button } from '@material-ui/core';
import './App.css';

function App() {
  const [input,setInput]=useState('');
  const [messages, setMessages]=useState(['hello'])

  console.log(input)
  console.log(messages)

  const sendMessage=(event)=>{
    event.preventDefault(); //it will prevent auto refresh when clicking submit button
    setMessages([...messages,input]);//It will add the input to the array
    setInput('');
  }

  return (
    <div className="App">
     <h1>Facebook Messenger</h1>
     <form>
     <input value={input} onChange={event => setInput(event.target.value)} />
     <Button variant="contained" color="primary" type="submit" onClick={sendMessage}>Send Message</Button>
     </form>

     {
       messages.map((message=>
        <p>{message}</p>))
     }
    </div>
  );
}

export default App;
